var messageDelay=2E3;$(init);function init(){$("#contactForm").hide().submit(submitForm).addClass("positioned");$('a[href="#contactForm"]').click(function(){$("#content").fadeTo("slow",0.2);$("#contactForm").fadeIn("slow",function(){$("#senderName").focus()});return false});$("#cancel").click(function(){$("#contactForm").fadeOut();$("#content").fadeTo("slow",1)});$("#contactForm").keydown(function(event){if(event.which==27){$("#contactForm").fadeOut();$("#content").fadeTo("slow",1)}})}
function submitForm(){var contactForm=$(this);if(!$("#senderName").val()||!$("#senderEmail").val()||!$("#message").val()){$("#incompleteMessage").fadeIn().delay(messageDelay).fadeOut();contactForm.fadeOut().delay(messageDelay).fadeIn()}else{$("#sendingMessage").fadeIn();contactForm.fadeOut();$.ajax({url:contactForm.attr("action")+"?ajax=true",type:contactForm.attr("method"),data:contactForm.serialize(),success:submitFinished})}return false}
function submitFinished(response){response=$.trim(response);$("#sendingMessage").fadeOut();if(response=="success"){$("#successMessage").fadeIn().delay(messageDelay).fadeOut();$("#senderName").val("");$("#senderEmail").val("");$("#message").val("");$("#content").delay(messageDelay+500).fadeTo("slow",1)}else{$("#failureMessage").fadeIn().delay(messageDelay).fadeOut();$("#contactForm").delay(messageDelay+500).fadeIn()}};

